#include <stdio.h>
#include <stdlib.h>
#include <math.h>
#include <inttypes.h>

// static long long euler = 0;

static char danumba[1001] = "";

int main(int argc, char** argv) {
	long long largest = 0;
	int temp[14] = {0};
	int i, j;
	long long numm, euler;

	for (i = 0; i < 1000-12; i++) {
		printf("%d ", i);
		for (j = 0; j < 13; j++) {
			temp[j] = danumba[j+i] - '0';
		}

		numm = (long long)temp[0] * (long long)temp[1] * (long long)temp[2] * (long long)temp[3] * (long long)temp[4] * (long long)temp[5] * (long long)temp[6] * (long long)temp[7] * (long long)temp[8] * (long long)temp[9] * (long long)temp[10] * (long long)temp[11] * (long long)temp[12];

		if (numm > largest) {
			printf("%d %d %d %d %d %d %d %d %d %d %d %d %d %d %d\n", i, j, temp[0], temp[1], temp[2], temp[3], temp[4], temp[5], temp[6], temp[7], temp[8], temp[9], temp[10], temp[11], temp[12]);
			printf("%llu %llu\n", largest, numm);
			largest = numm;
		}
	}

	euler = largest;

	printf("%llu\n", euler);

return 0;
}

